import React, { useState } from 'react';
import './App.css';
import Filtre from './Filtre';
import ListeFilms from './ListeFilms';
import CustomNav from './CustomNav';




function App() {
  
  const [films, setFilms] = useState([
    {
      title: "La legende aux sabres ",
      description: "Dans un monde ancien et mystique, une légende raconte l'histoire d'un guerrier légendaire dont l'habileté avec un sabre est inégalée.Lorsque des forces obscures menacent d'engloutir le royaume, le guerrier doit puiser dans son passé tumultueux...",
      posterURL: "data:image/jpeg;base64,",
      rating: 9,
    },
    {
      title: "Le Survivant",
      description: "Après une catastrophe mondiale dévastatrice, un homme se retrouve comme le dernier survivant dans une ville en ruines. Isolé, il doit faire face à la solitude et à la désolation tout en cherchant des signes de vie..",
      posterURL: "data:image/jpeg;base64,",
      rating: 9.3,
    },
   
  ]);

  const [filmsFiltres, setFilmsFiltres] = useState([]);
  const [titreFiltre, setTitreFiltre] = useState("");
  const [noteFiltre, setNoteFiltre] = useState(null);

  const [nouveauFilm, setNouveauFilm] = useState({
    title: "",
    description: "",
    posterURL: "",
    rating: 0,
  });

  const handleTitreChange = (titre) => {
    setTitreFiltre(titre);
    filtrerFilms(titre, noteFiltre);
  };

  const handleNoteChange = (note) => {
    setNoteFiltre(note === "" ? null : parseFloat(note));
    filtrerFilms(titreFiltre, note === "" ? null : parseFloat(note));
  };

  const handleNouveauFilmChange = (e) => {
    const { name, value } = e.target;
    setNouveauFilm((prevFilm) => ({
      ...prevFilm,
      [name]: value,
    }));
  };

  const ajouterFilm = () => {
    setFilms((prevFilms) => [...prevFilms, nouveauFilm]);
    setNouveauFilm({
      title: "",
      description: "",
      posterURL: "",
      rating: 0,
    });
  };

  const filtrerFilms = (titre, note) => {
    const filmsFiltres = films.filter((film) => {
      const titreCorrespond = film.title.toLowerCase().includes(titre.toLowerCase());
      const noteCorrespond = note === null || film.rating >= note;
      return titreCorrespond && noteCorrespond;
    });

    setFilmsFiltres(filmsFiltres);
  };

  return (
    <div className="app">
      <h2 className='ti'>application de cinéma </h2>  
      <Filtre
        onTitleChange={handleTitreChange}
        onRatingChange={handleNoteChange}
      />

      <div className="ajouter-film">
        <h2>Ajouter un Film</h2>
        <input
          type="text"
          name="title"
          placeholder="Titre"
          value={nouveauFilm.title}
          onChange={handleNouveauFilmChange}
        />
        <input
          type="text"
          name="description"
          placeholder="Description"
          value={nouveauFilm.description}
          onChange={handleNouveauFilmChange}
        />
        <input
          type="text"
          name="posterURL"
          placeholder="URL de l'affiche"
          value={nouveauFilm.posterURL}
          onChange={handleNouveauFilmChange}
        />
        <input
          type="number"
          name="rating"
          placeholder="Note"
          value={nouveauFilm.rating}
          onChange={handleNouveauFilmChange}
        />
        <button onClick={ajouterFilm}>Ajouter un Film</button>
      </div>

      <ListeFilms films={filmsFiltres.length > 0 ? filmsFiltres : films} />
    </div>
  );
}

export default App;